import React, { useState, useRef, useEffect } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import { useHistory } from 'react-router';
import NavigationBar from '../../HomePage/components/NavigationBar';
import Sidebar from '../../HomePage/components/Sidebar';
import SidebarStudent from '../../HomePage/components/SidebarStudent';
import validator from 'validator';
function changeDateFormat(responseDate) {
  var date = new Date(responseDate);
  var min = date.getMinutes();
  var hour = date.getHours();
  var second = date.getSeconds();
  var day = date.getUTCDate();
  var month = date.getUTCMonth() + 1;
  var year = date.getFullYear();

  month = (month > 9 ? '' : '0') + month;
  day = (day > 9 ? '' : '0') + day;

  return `${year}-${month}-${day}`;
}
function ChangeInfoTeacher() {
  const role = JSON.parse(sessionStorage.getItem('user'))
    ? JSON.parse(sessionStorage.getItem('user')).role
    : '';
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [dateOfBirth, setDateOfBirth] = useState('');
  const [fieldOfStudy, setFieldOfStudy] = useState('');
  const [introduction, setIntroduction] = useState('');
  const username = JSON.parse(sessionStorage.getItem('user'))
    ? JSON.parse(sessionStorage.getItem('user')).username
    : '';
  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/users/${username}/show`)
      .then((res) => {
        setName(res.data.name);
        setEmail(res.data.email);
        setPhone(res.data.phoneNumber);
        setDateOfBirth(changeDateFormat(res.data.dateOfBirth));
        setFieldOfStudy(res.data.fieldOfStudy);
        setIntroduction(res.data.introduction);
      })
      .catch((err) => {
        setSuccessMessage('Cập nhật thông tin thất bại !');
      });
  }, []);
  const [successMessage, setSuccessMessage] = useState('');
  const [nameErrorMessage, setNameErrorMessage] = useState('');
  const [emailErrorMessage, setEmailErrorMessage] = useState('');
  const [phoneErrorMessage, setPhoneErrorMessage] = useState('');
  const handleName = (e) => {
    setName(e.target.value);
    setNameErrorMessage('');
    setSuccessMessage('');
  };
  const handleEmail = (e) => {
    setEmail(e.target.value);
    setEmailErrorMessage('');
    setSuccessMessage('');
  };
  const handlePhone = (e) => {
    setPhone(e.target.value);
    setPhoneErrorMessage('');
    setSuccessMessage('');
  };
  const handleDateOfBirth = (e) => {
    setDateOfBirth(e.target.value);
    setSuccessMessage('');
  };
  const handleFieldOfStudy = (e) => {
    setFieldOfStudy(e.target.value);
    setSuccessMessage('');
  };
  const handleIntroduction = (e) => {
    setIntroduction(e.target.value);
    setSuccessMessage('');
  };
  const stringContainsNumber = (_string) => {
    if (_string.length <= 0) {
      return true;
    } else {
      return /\d/.test(_string);
    }
  };
  const phoneNumberValidator = (_phone) => {
    if (/[a-zA-Z]/g.test(_phone) || _phone.length != 10) {
      return false;
    }
    return true;
  };
  const ref = useRef();
  const history = useHistory();
  const handleSubmit = (e) => {
    if (
      validator.isEmail(email) &&
      phoneNumberValidator(phone) &&
      !stringContainsNumber(name)
    ) {
      axios
        .post(`http://localhost:5000/api/users/${username}/update`, {
          email: email,
          name: name,
          phoneNumber: phone,
          dateOfBirth: dateOfBirth,
          fieldOfStudy: fieldOfStudy,
          introduction: introduction,
        })
        .then((res) => {
          if (res.data.success) {
            setSuccessMessage('Cập nhật thông tin thành công');
          }
        })
        .catch((err) => {
          setSuccessMessage('Cập nhật thông tin thất bại');
          console.log(err);
        });
    }
    if (stringContainsNumber(name)) {
      setNameErrorMessage('Tên không hợp lệ');
    }
    if (!validator.isEmail(email)) {
      setEmailErrorMessage('Email không hợp lệ');
    }
    if (!phoneNumberValidator(phone)) {
      setPhoneErrorMessage('Số điện thoại không hợp lệ');
    }
    e.preventDefault();
  };
  return (
    <Wrapper>
      <NavigationBar />
      <Container>
        <h1> Thông tin cá nhân </h1>
        <div className="line">
          <div className="FullName">
            <p className="FieldToChange">
              Họ và tên: <Red>*</Red>
              {nameErrorMessage && (
                <ErrorMessage>{nameErrorMessage}</ErrorMessage>
              )}
            </p>
            <div className="Field2">
              <Input
                name="FullName"
                id="FullName"
                style={{ width: '400px', height: '55px;' }}
                value={name}
                type="text"
                onChange={handleName}
              />
            </div>
          </div>
          <div className="DateOfBirth">
            <p className="FieldToChange">
              Ngày sinh: <Red>*</Red>
            </p>
            <div className="Field2">
              {/* <Input
                name="Day"
                id="Day"
                style={{ width: '100px', height: '55px' }}
                placeholder="01"
              />
              <p className="Slash">/</p>
              <Input
                name="Month"
                id="Month"
                style={{ width: '100px', height: '55px' }}
                placeholder="01"
              />
              <p className="Slash">/</p>
              <Input
                name="Year"
                id="Year"
                style={{ width: '100px', height: '55px' }}
                placeholder="2000"
              /> */}
              <Input
                name="FullName"
                id="FullName"
                style={{ width: '400px', height: '55px' }}
                type="text"
                value={dateOfBirth}
                ref={ref}
                onFocus={() => (ref.current.type = 'date')}
                onBlur={() => {
                  ref.current.type = 'text';
                  ref.current.value = dateOfBirth;
                }}
                onChange={handleDateOfBirth}
              />
            </div>
          </div>
        </div>

        <div className="line">
          <div className="Email">
            <p className="FieldToChange">
              Email: <Red>*</Red>
              {emailErrorMessage && (
                <ErrorMessage>{emailErrorMessage}</ErrorMessage>
              )}
            </p>
            <div className="Field2">
              <Input
                name="Email"
                id="Email"
                style={{ width: '400px', height: '55px;' }}
                value={email}
                type="email"
                onChange={handleEmail}
              />
            </div>
          </div>
          <div className="Identifier">
            {role === 'Teacher' ? (
              <p className="FieldToChange">Mã số giảng viên: </p>
            ) : (
              <p className="FieldToChange">Mã số sinh viên: </p>
            )}
            <div className="Field2">
              <Input
                name="ConstID"
                id="ConstID"
                type="text"
                value={username}
                disabled
                style={{
                  width: '400px',
                  height: '55px',
                  backgroundColor: '#404E68',
                  color: 'white',
                }}
              />
            </div>
          </div>
        </div>

        <div className="line">
          <div className="Major">
            <p className="FieldToChange">Chuyên ngành: </p>
            <div className="Field2">
              <Input
                name="Major"
                id="Major"
                type="text"
                style={{ width: '400px', height: '55px;' }}
                value={fieldOfStudy}
                onChange={handleFieldOfStudy}
              />
            </div>
          </div>
          <div className="Phone">
            <p className="FieldToChange">
              Số điện thoại:
              {phoneErrorMessage && (
                <ErrorMessage>{phoneErrorMessage}</ErrorMessage>
              )}
            </p>
            <div className="Field2">
              <Input
                name="Phone"
                id="Phone"
                type="tel"
                style={{ width: '400px', height: '55px;' }}
                value={phone}
                onChange={handlePhone}
              />
            </div>
          </div>
        </div>

        <div className="desc">
          <div>
            <p className="FieldToChange">Giới thiệu: </p>
            <div className="Field1">
              <Textarea
                type="textarea"
                name="Description"
                id="Description"
                style={{
                  width: '100%',
                  height: '140px',
                }}
                value={introduction}
                onChange={handleIntroduction}
              ></Textarea>
            </div>
          </div>
        </div>
        {successMessage &&
          (successMessage === 'Cập nhật thông tin thành công' ? (
            <SuccessMessage style={{ color: 'blue' }}>
              {successMessage}
            </SuccessMessage>
          ) : (
            <SuccessMessage style={{ color: 'red' }}>
              {successMessage}
            </SuccessMessage>
          ))}
        <div className="wrapper">
          <Button
            id="cancel"
            onClick={(e) => {
              if (role === 'Student') {
                history.push('/studentHomepage');
              } else if (role === 'Teacher') {
                history.push('/teacherHomepage');
              }
              e.preventDefault();
            }}
          >
            <p className="Cancel">Hủy</p>
          </Button>
          <Button id="submit" onClick={handleSubmit}>
            <p className="Submit">Cập nhật</p>
          </Button>
        </div>
      </Container>
      {role === 'Teacher' ? <Sidebar /> : <SidebarStudent />}
    </Wrapper>
  );
}
