import React, { useEffect, useState } from 'react';
import { Bar } from 'react-chartjs-2';
import axios from 'axios';

const BarChart = ({ selectedClassID }) => {
  const [gpaList, setGpaList] = useState([]);
  useEffect(() => {
    if (selectedClassID !== undefined) {
      axios
        .get(`http://localhost:5000/api/classes/${selectedClassID}/grades`)
        .then((res) => {
          setGpaList(res.data.gpa);
        })
        .catch((err) => {
          console.log(err);
        });
    }
  }, [selectedClassID]);
  const data = {
    labels: [
      '<2.0',
      '2.0',
      '2.1',
      '2.2',
      '2.3',
      '2.4',
      '2.5',
      '2.6',
      '2.7',
      '2.8',
      '2.9',
      '3.0',
      '3.1',
      '3.2',
      '3.3',
      '3.4',
      '3.5',
      '3.6',
      '3.7',
      '3.8',
      '3.9',
      '4.0',
    ],
    datasets: [
      {
        label: 'Số lượng điểm GPA',
        data: gpaList,
        backgroundColor: [
          'rgba(64, 78, 104, 0.85)',
          'rgba(54, 162, 235, 0.85)',
          'rgba(255, 206, 86, 0.85)',
          'rgba(75, 192, 192, 0.85)',
          'rgba(153, 102, 255, 0.85)',
          'rgba(255, 159, 64, 0.85)',
        ],
        hoverBackgroundColor: [
          'rgba(64, 78, 104, 1)',
          'rgba(54, 162, 235, 1)',
          'rgba(255, 206, 86, 1)',
          'rgba(75, 192, 192, 1)',
          'rgba(153, 102, 255, 1)',
          'rgba(255, 159, 64, 1)',
        ],
      },
    ],
  };
  return (
    <>
      <h1 style={{ textAlign: 'center', fontSize: '50px' }}>GPA</h1>
      <Bar data={data} height={50} gpaList={gpaList} setGpaList={setGpaList} />
    </>
  );
};

export default BarChart;
